declare module 'spdl-core' {
  import { Readable } from 'stream';

  namespace ytdl {
    interface trackInfo {
      title: string;
      artist: string[];
      url: string;
      id: string;
      uri: string;
    }

    function setCredentials(clientId: string, clientSecret: string): void;
    function setClientId(clientId: string): void;
    function setClientSecret(clientSecret: string): void;
    function getURLTrackID(url: string): string | never;
    function getInfo(url: string): Promise<trackInfo>;
    function validateURL(url: string): boolean;
  }

  function ytdl(link: string): Promise<Readable>;
  
  export = ytdl;
}
